mapper.select:
select count(*) from tbl_tran as t join tbl_user as u on t.owner = u.id join tbl_customer as c on t.customerId = c.id
u.name like '%' #{owner} '%'
t.name like '%' #{name}'%'
c.name like '%' #{customer} '%'
t.stage = #{stage}
t.type = #{type}
t.source = #{source}

select t.id as id, t.name as name,cus.name as customerId,t.stage as stage, t.type as type,u.name as owner,t.source as source,con.fullname as contactsId from tbl_tran as t join tbl_user as u on t.owner = u.id join tbl_customer as cus on t.customerId = cus.id left join tbl_contacts as con on t.contactsId = con.id
u.name like '%' #{owner} '%'
t.name like '%' #{name}'%'
cus.name like '%' #{customer} '%'
t.stage = #{stage}
t.type = #{type}
t.source = #{source}
con.fullname like '%' #{contacts} '%'
order by t.createTime desc limit #{pageStart},#{pageSize}

mapper.update:
update tbl_tran set
    stage=#{stage},
    editBy=#{editBy},
    editTime=#{editTime}
where id=#{id}
